def binarySearch(arr, target):
    low, high = 0, len(arr)-1
    while low <= high:
        mid = (low+high)//2
        if arr[mid] == target:
            return mid
        elif arr[mid] < target:
            low = mid+1
        else:
            high = mid-1
    return -1

def mergeSort(arr):
    if len(arr) <= 1:
        return arr
    mid = len(arr)//2
    left = mergeSort(arr[:mid])
    right = mergeSort(arr[mid:])
    return merge(left, right)

def merge(left, right):
    result = []
    i=j=0
    while i<len(left) and j<len(right):
        if left[i]<right[j]:
            result.append(left[i])
            i+=1
        else:
            result.append(right[j])
            j+=1
    result.extend(left[i:])
    result.extend(right[j:])
    return result

def quickSort(arr):
    if len(arr) <= 1:
        return arr
    pivot = arr[len(arr)//2]
    left = [x for x in arr if x < pivot]
    middle = [x for x in arr if x == pivot]
    right = [x for x in arr if x > pivot]
    return quickSort(left) + middle + quickSort(right)

def findMinMax(arr, low, high):
    if low == high:
        return (arr[low], arr[low])
    if high == low+1:
        return (min(arr[low], arr[high]), max(arr[low], arr[high]))
    mid = (low + high)//2
    min1, max1 = findMinMax(arr, low, mid)
    min2, max2 = findMinMax(arr, mid+1, high)
    return (min(min1, min2), max(max1, max2))

def kthSmallest(arr, k):
    arr.sort()
    return arr[k-1]

def majorityElement(nums):
    count, candidate = 0, None
    for num in nums:
        if count == 0:
            candidate = num
        count += (1 if num == candidate else -1)
    return candidate

def findMedianSortedArrays(nums1, nums2):
    nums = sorted(nums1 + nums2)
    n = len(nums)
    if n % 2 == 0:
        return (nums[n//2 - 1] + nums[n//2]) / 2
    else:
        return nums[n//2]

def power(x, n):
    if n == 0:
        return 1
    temp = power(x, n//2)
    if n % 2 == 0:
        return temp * temp
    else:
        return x * temp * temp

import heapq

def mergeKLists(lists):
    heap = []
    for l in lists:
        for val in l:
            heapq.heappush(heap, val)
    result = []
    while heap:
        result.append(heapq.heappop(heap))
    return result

import math

def dist(p1, p2):
    return math.sqrt((p1[0]-p2[0])**2 + (p1[1]-p2[1])**2)

def closestPair(points):
    points.sort()
    return closestUtil(points)

def closestUtil(points):
    n = len(points)
    if n <= 3:
        return min(dist(points[i], points[j]) for i in range(n) for j in range(i+1, n))
    mid = n//2
    d = min(closestUtil(points[:mid]), closestUtil(points[mid:]))
    mid_x = points[mid][0]
    strip = [p for p in points if abs(p[0]-mid_x) < d]
    strip.sort(key=lambda x: x[1])
    for i in range(len(strip)):
        for j in range(i+1, len(strip)):
            if (strip[j][1] - strip[i][1]) >= d:
                break
            d = min(d, dist(strip[i], strip[j]))
    return d
