def longestMountain(arr):
    n = len(arr)
    res = 0
    i = 1
    while i < n-1:
        if arr[i-1] < arr[i] > arr[i+1]:
            l, r = i, i
            while l > 0 and arr[l-1] < arr[l]:
                l -= 1
            while r < n-1 and arr[r] > arr[r+1]:
                r += 1
            res = max(res, r-l+1)
            i = r
        else:
            i += 1
    return res

def countPairs(arr, target):
    arr.sort()
    i, j = 0, len(arr) - 1
    count = 0
    while i < j:
        if arr[i] + arr[j] < target:
            count += (j - i)
            i += 1
        else:
            j -= 1
    return count

