def trap(height):
    l, r = 0, len(height)-1
    leftMax, rightMax = 0, 0
    res = 0
    while l < r:
        if height[l] < height[r]:
            if height[l] >= leftMax:
                leftMax = height[l]
            else:
                res += leftMax - height[l]
            l += 1
        else:
            if height[r] >= rightMax:
                rightMax = height[r]
            else:
                res += rightMax - height[r]
            r -= 1
    return res

def validPalindrome(s):
    def is_pali(i, j):
        while i < j:
            if s[i] != s[j]:
                return False
            i += 1
            j -= 1
        return True

    i, j = 0, len(s) - 1
    while i < j:
        if s[i] != s[j]:
            return is_pali(i + 1, j) or is_pali(i, j - 1)
        i += 1
        j -= 1
    return True

