from collections import deque
def bfs(graph, start):
    visited = set()
    order = []
    q = deque([start])
    while q:
        node = q.popleft()
        if node not in visited:
            visited.add(node)
            order.append(node)
            for neighbor in graph.get(node, []):
                if neighbor not in visited:
                    q.append(neighbor)
    return order

def dfs(graph, start):
    visited = set()
    order = []
    def helper(node):
        if node not in visited:
            visited.add(node)
            order.append(node)
            for neighbor in graph.get(node, []):
                helper(neighbor)
    helper(start)
    return order

def hasCycleUndirected(graph, n):
    visited = [False]*n
    def dfs(u, parent):
        visited[u] = True
        for v in graph.get(u, []):
            if not visited[v]:
                if dfs(v, u):
                    return True
            elif v != parent:
                return True
        return False
    for i in range(n):
        if not visited[i]:
            if dfs(i, -1):
                return True
    return False

def hasCycleDirected(graph, n):
    visited = [0]*n
    def dfs(u):
        if visited[u] == 1:
            return True
        if visited[u] == 2:
            return False
        visited[u] = 1
        for v in graph.get(u, []):
            if dfs(v):
                return True
        visited[u] = 2
        return False
    for i in range(n):
        if visited[i] == 0:
            if dfs(i):
                return True
    return False

from collections import deque, defaultdict
def topoSort(graph, n):
    indegree = [0]*n
    for u in graph:
        for v in graph[u]:
            indegree[v] += 1
    q = deque([i for i in range(n) if indegree[i]==0])
    order = []
    while q:
        u = q.popleft()
        order.append(u)
        for v in graph.get(u, []):
            indegree[v] -= 1
            if indegree[v] == 0:
                q.append(v)
    return order if len(order)==n else []

def numIslands(grid):
    if not grid: return 0
    n, m = len(grid), len(grid[0])
    def dfs(i, j):
        if i<0 or i>=n or j<0 or j>=m or grid[i][j]!='1': return
        grid[i][j] = '#'
        for di, dj in [(-1,0),(1,0),(0,-1),(0,1)]:
            dfs(i+di, j+dj)
    count = 0
    for i in range(n):
        for j in range(m):
            if grid[i][j] == '1' or grid[i][j] == 1:
                dfs(i, j)
                count += 1
    return count

class Node:
    def __init__(self, val, neighbors=None):
        self.val = val
        self.neighbors = neighbors if neighbors else []
def cloneGraph(node):
    if not node: return None
    old_to_new = {}
    def dfs(n):
        if n in old_to_new:
            return old_to_new[n]
        copy = Node(n.val)
        old_to_new[n] = copy
        for nei in n.neighbors:
            copy.neighbors.append(dfs(nei))
        return copy
    return dfs(node)

def isBipartite(graph):
    color = {}
    for node in range(len(graph)):
        if node not in color:
            stack = [node]
            color[node] = 0
            while stack:
                u = stack.pop()
                for v in graph[u]:
                    if v not in color:
                        color[v] = color[u]^1
                        stack.append(v)
                    elif color[v] == color[u]:
                        return False
    return True

def countComponents(n, edges):
    parent = list(range(n))
    def find(x):
        while parent[x] != x:
            parent[x] = parent[parent[x]]
            x = parent[x]
        return x
    for u, v in edges:
        parent[find(u)] = find(v)
    return len(set(find(x) for x in range(n)))

from collections import deque
def shortestPath(graph, start, end):
    visited = set([start])
    q = deque([(start, 0)])
    while q:
        node, dist = q.popleft()
        if node == end:
            return dist
        for nei in graph.get(node, []):
            if nei not in visited:
                visited.add(nei)
                q.append((nei, dist+1))
    return -1

from collections import deque
def ladderLength(beginWord, endWord, wordList):
    wordSet = set(wordList)
    if endWord not in wordSet:
        return 0
    q = deque([(beginWord, 1)])
    while q:
        word, length = q.popleft()
        if word == endWord:
            return length
        for i in range(len(word)):
            for c in 'abcdefghijklmnopqrstuvwxyz':
                nextWord = word[:i] + c + word[i+1:]
                if nextWord in wordSet:
                    wordSet.remove(nextWord)
                    q.append((nextWord, length+1))
    return 0

def allPathsSourceTarget(graph):
    res = []
    def dfs(path, node):
        if node == len(graph)-1:
            res.append(path[:])
            return
        for nei in graph[node]:
            dfs(path+[nei], nei)
    dfs([0], 0)
    return res

def isTree(n, edges):
    if len(edges) != n-1:
        return False
    parent = list(range(n))
    def find(x):
        while parent[x] != x:
            parent[x] = parent[parent[x]]
            x = parent[x]
        return x
    for u, v in edges:
        pu, pv = find(u), find(v)
        if pu == pv:
            return False
        parent[pu] = pv
    return len(set(find(x) for x in range(n))) == 1

def count_paths(graph, src, dst, visited=None):
    if visited is None:
        visited = set()
    if src == dst:
        return 1
    visited.add(src)
    total = 0
    for neighbor in graph.get(src, []):
        if neighbor not in visited:
            total += count_paths(graph, neighbor, dst, visited.copy())
    return total

import heapq
def dijkstra(graph, src):
    n = len(graph)
    dist = [float('inf')] * n
    dist[src] = 0
    heap = [(0, src)]
    while heap:
        d, u = heapq.heappop(heap)
        if d > dist[u]:
            continue
        for v, w in graph.get(u, []):
            if dist[v] > dist[u] + w:
                dist[v] = dist[u] + w
                heapq.heappush(heap, (dist[v], v))
    return dist

def bellman_ford(n, edges, src):
    dist = [float('inf')] * n
    dist[src] = 0
    for _ in range(n-1):
        for u, v, w in edges:
            if dist[u] != float('inf') and dist[v] > dist[u] + w:
                dist[v] = dist[u] + w
    for u, v, w in edges:
        if dist[u] != float('inf') and dist[v] > dist[u] + w:
            return 'Negative cycle'
    return dist

