def build_adj_list(n, edges):
    adj = defaultdict(list)
    for u, v in edges:
        adj[u].append(v)
    return adj

def bfs(adj, start):
    visited = set()
    queue = deque([start])
    order = []
    while queue:
        node = queue.popleft()
        if node not in visited:
            visited.add(node)
            order.append(node)
            for neighbor in adj[node]:
                if neighbor not in visited:
                    queue.append(neighbor)
    return order

def dfs(adj, start, visited=None, order=None):
    if visited is None:
        visited = set()
    if order is None:
        order = []
    visited.add(start)
    order.append(start)
    for neighbor in adj[start]:
        if neighbor not in visited:
            dfs(adj, neighbor, visited, order)
    return order
