def is_palindrome(s):
    cleaned = ''.join(c.lower() for c in s if c.isalnum())
    return cleaned == cleaned[::-1]

def is_anagram(s1, s2):
    from collections import Counter
    s1_clean = ''.join(c.lower() for c in s1 if c.isalnum())
    s2_clean = ''.join(c.lower() for c in s2 if c.isalnum())
    return Counter(s1_clean) == Counter(s2_clean)

def longest_common_prefix(strs):
    if not strs:
        return ""
    prefix = strs[0]
    for s in strs[1:]:
        while not s.startswith(prefix):
            prefix = prefix[:-1]
            if not prefix:
                return ""
    return prefix

def remove_duplicates(s):
    seen = set()
    result = []
    for ch in s:
        if ch not in seen:
            seen.add(ch)
            result.append(ch)
    return ''.join(result)

def length_of_longest_substring(s):
    char_index = {}
    left = 0
    max_length = 0
    for right, ch in enumerate(s):
        if ch in char_index and char_index[ch] >= left:
            left = char_index[ch] + 1
        char_index[ch] = right
        max_length = max(max_length, right - left + 1)
    return max_length

from collections import Counter

def find_anagrams(s, p):
    p_count = Counter(p)
    s_count = Counter(s[:len(p)])
    result = []
    if s_count == p_count:
        result.append(0)
    for i in range(len(p), len(s)):
        s_count[s[i]] += 1
        s_count[s[i - len(p)]] -= 1
        if s_count[s[i - len(p)]] == 0:
            del s_count[s[i - len(p)]]
        if s_count == p_count:
            result.append(i - len(p) + 1)
    return result

def count_and_say(n):
    if n == 1:
        return "1"
    prev = "1"
    for _ in range(2, n + 1):
        curr = ""
        count = 1
        for i in range(1, len(prev)):
            if prev[i] == prev[i - 1]:
                count += 1
            else:
                curr += str(count) + prev[i - 1]
                count = 1
        curr += str(count) + prev[-1]
        prev = curr
    return prev

def longest_palindromic_substring(s):
    def expand_around_center(left, right):
        while left >= 0 and right < len(s) and s[left] == s[right]:
            left -= 1
            right += 1
        return s[left + 1:right]
    if not s:
        return ""
    longest = ""
    for i in range(len(s)):
        odd_palindrome = expand_around_center(i, i)
        even_palindrome = expand_around_center(i, i + 1)
        longest = max(longest, odd_palindrome, even_palindrome, key=len)
    return longest

def my_atoi(s):
    s = s.strip()
    if not s:
        return 0
    sign = 1
    if s[0] == '-':
        sign = -1
        s = s[1:]
    elif s[0] == '+':
        s = s[1:]
    result = 0
    for char in s:
        if char.isdigit():
            result = result * 10 + int(char)
        else:
            break
    result *= sign
    INT_MAX, INT_MIN = 2147483647, -2147483648
    return min(max(result, INT_MIN), INT_MAX)

def convert(s, num_rows):
    if num_rows == 1:
        return s
    result = [''] * num_rows
    current_row, going_down = 0, False
    for char in s:
        result[current_row] += char
        if current_row == 0 or current_row == num_rows - 1:
            going_down = not going_down
        current_row += 1 if going_down else -1
    return ''.join(result)

def word_break(s, word_dict):
    dp = [False] * (len(s) + 1)
    dp[0] = True
    for i in range(1, len(s) + 1):
        for j in range(i):
            if dp[j] and s[j:i] in word_dict:
                dp[i] = True
                break
    return dp[len(s)]

def word_pattern(pattern, str):
    words = str.split()
    if len(pattern) != len(words):
        return False
    pattern_map = {}
    word_map = {}
    for p, w in zip(pattern, words):
        if pattern_map.get(p, w) != w or word_map.get(w, p) != p:
            return False
        pattern_map[p] = w
        word_map[w] = p
    return True

def partition(s):
    def is_palindrome(sub):
        return sub == sub[::-1]
    def backtrack(start, path):
        if start == len(s):
            result.append(path[:])
            return
        for end in range(start + 1, len(s) + 1):
            substring = s[start:end]
            if is_palindrome(substring):
                path.append(substring)
                backtrack(end, path)
                path.pop()
    result = []
    backtrack(0, [])
    return result)

def can_permute_palindrome(s):
    freq = {}
    for char in s:
        freq[char] = freq.get(char, 0) + 1
    odd_count = sum(1 for count in freq.values() if count % 2 != 0)
    return odd_count <= 1

from collections import defaultdict

def group_anagrams(strs):
    anagrams = defaultdict(list)
    for s in strs:
        sorted_str = ''.join(sorted(s))
        anagrams[sorted_str].append(s)
    return list(anagrams.values())
