def word_break(s, word_dict):
    dp = [False] * (len(s) + 1)
    dp[0] = True
    for i in range(1, len(s) + 1):
        for j in range(i):
            if dp[j] and s[j:i] in word_dict:
                dp[i] = True
                break
    return dp[len(s)]

def word_pattern(pattern, str):
    words = str.split()
    if len(pattern) != len(words):
        return False
    pattern_map = {}
    word_map = {}
    for p, w in zip(pattern, words):
        if pattern_map.get(p, w) != w or word_map.get(w, p) != p:
            return False
        pattern_map[p] = w
        word_map[w] = p
    return True

